CREATE TABLE `gwc_drivers` ( `id` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT , `first_name` VARCHAR(150) CHARACTER SET utf8 COLLATE utf8_persian_ci NULL DEFAULT NULL , `last_name` VARCHAR(150) CHARACTER SET utf8 COLLATE utf8_persian_ci NULL DEFAULT NULL , `avatar` VARCHAR(150) CHARACTER SET utf8 COLLATE utf8_persian_ci NULL DEFAULT NULL , `username` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_persian_ci NOT NULL , `phone` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_persian_ci NULL DEFAULT NULL , `password` VARCHAR(150) NOT NULL , `is_active` BOOLEAN NOT NULL DEFAULT TRUE , `created_at` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;
ALTER TABLE `gwc_drivers` ADD `deleted_at` DATETIME NULL DEFAULT NULL AFTER `is_active`;
ALTER TABLE `gwc_drivers` CHANGE `first_name` `first_name_en` VARCHAR(150) CHARACTER SET utf8 COLLATE utf8_persian_ci NULL DEFAULT NULL, CHANGE `last_name` `last_name_en` VARCHAR(150) CHARACTER SET utf8 COLLATE utf8_persian_ci NULL DEFAULT NULL;
ALTER TABLE `gwc_drivers` ADD `first_name_ar` VARCHAR(150) CHARACTER SET utf8 COLLATE utf8_persian_ci NULL DEFAULT NULL AFTER `last_name_en`, ADD `last_name_ar` VARCHAR(150) CHARACTER SET utf8 COLLATE utf8_persian_ci NULL DEFAULT NULL AFTER `first_name_ar`;
ALTER TABLE `gwc_orders_details` ADD `driver_id` BIGINT UNSIGNED NULL DEFAULT NULL AFTER `order_ip`, ADD `driver_assigner_id` BIGINT UNSIGNED NULL DEFAULT NULL AFTER `driver_id`, ADD `assigned_at` TIMESTAMP NULL DEFAULT NULL AFTER `driver_assigner_id`;